/**
 * Represents an error that is caused by invalid tool call request.
 *
 * @public
 * @experimental [EXP-GRANULAR-ACT] More granular .act status reporting is experimental and may
 * change in the future
 */
export class ToolCallRequestError extends Error {
  public constructor(
    message: string,
    /**
     * The raw output that was generated by the model before the tool call. The exact nature of this
     * fields depends on the error. It sometimes include the entire tool calls section, or sometimes
     * just the single tool call that failed.
     *
     * This field is not always available, and may be `undefined`.
     *
     * @experimental [EXP-GRANULAR-ACT] More granular .act status reporting is experimental and may
     * change in the future
     * @experimental [EXP-NON-ACT-TOOL-CALLBACKS] Tool call callbacks in .respond/.complete is in an
     * experimental feature. This may change in the future without warning.
     */
    public readonly rawContent: string | undefined,
  ) {
    super(message);
  }
}

/**
 * Represents an error that is caused by an invalid tool name.
 *
 * @public
 * @experimental [EXP-GRANULAR-ACT] More granular .act status reporting is experimental and may
 * change in the future
 */
export class ToolCallRequestInvalidNameError extends ToolCallRequestError {
  public constructor(
    message: string,
    rawContent: string | undefined,
    public readonly toolName: string,
  ) {
    super(message, rawContent);
  }
}

/**
 * Represents an error that is caused by invalid tool arguments.
 *
 * @public
 * @experimental [EXP-GRANULAR-ACT] More granular .act status reporting is experimental and may
 * change in the future
 */
export class ToolCallRequestInvalidArgumentsError extends ToolCallRequestError {
  public constructor(
    message: string,
    rawContent: string | undefined,
    public readonly toolName: string,
    public readonly toolArguments: Record<string, any> | undefined,
  ) {
    super(message, rawContent);
  }
}

/**
 * Represents an error that is caused by invalid tool arguments.
 *
 * @public
 * @experimental [EXP-GRANULAR-ACT] More granular .act status reporting is experimental and may
 * change in the future
 */
export class ToolCallRequestInvalidFormatError extends ToolCallRequestError {
  public constructor(message: string, rawContent: string | undefined) {
    super(message, rawContent);
  }
}
